// Đức phật nơi đây phù hộ code con chạy không Bug. Nam mô a di đà Phật
enum HomeItemType {
    case plant
    case bug
}
class HomeViewModel: BaseViewModel{
    var items = MutableObservableArray<String>.init([])
    
    func loadAPI(image : UIImage , type: HomeItemType ,complete: @escaping (([ItemModel]?) -> Void)) {
        
        switch type {
        case .plant:
            RestAPI.shared.getPlantIdentify(image: image) { results in
                if results == nil {
                    complete(nil)
                }
                else {
                    complete(results!)
                }
            }
        case .bug:
            RestAPI.shared.getBugIdentify(image: image) { results in
                if results == nil {
                    complete(nil)
                }
                else {
                    complete(results!)
                }
            }
        }
    }
    deinit {
        print("Deinit: " + String(describing: type(of: self)))
    }

}
