enum ResultRouter: RouterType {
    case moreResults(moreItemModel : [ItemModel], identifyType: HomeItemType, userImage: UIImage)
}

class ResultNavigator: BaseNavigator<ResultRouter> {
    override func navigate(to:ResultRouter) {
        switch to {
        case .moreResults(let moreItemModel, let type, let image):
            let vm = MoreResultViewModel()
            vm.moreResulstArray = moreItemModel
            vm.itemIdentifyType = type
            vm.userImage = image
            let nav = MoreResultNavigator()
            let vc = MoreResultController(viewModel: vm, navigator: nav)
            viewController?.navigationController?.pushViewController(vc, animated: true)
        }
    }

    deinit {
        print("Deinit: " + String(describing: type(of: self)))
    }

}
